pragma solidity >=0.8.0 <0.9.0;

import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Permit.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

interface IEthenaStaking {
    function stake(address user, uint256 amount) external returns (uint256 sUSDeAmount);
}

contract Loan {
    IERC20Permit public usdcTokenPermit;  // For permit functionality
    IERC20 public usdcToken;  // For balanceOf function
    IERC20 public usdEToken;
    address public aavePool;  // Just the address, no need for IPool interface
    IEthenaStaking public ethenaStaking;

    address public deployer;

    struct UserDeposit {
        uint256 usdEAmount;
        uint256 sUsdEAmount;
        uint256 usdcAmount;
        uint256 aEthUsdcAmount;
    }

    mapping(address => UserDeposit) public userDeposits;

    constructor(address _usdEToken, address _usdcToken, address _aavePool, address _ethenaStaking, address _deployer) {
        usdEToken = IERC20(_usdEToken);
        usdcTokenPermit = IERC20Permit(_usdcToken);  // IERC20Permit for permit functionality
        usdcToken = IERC20(_usdcToken);  // IERC20 for balanceOf functionality
        aavePool = _aavePool;  // Just store the address
        ethenaStaking = IEthenaStaking(_ethenaStaking);
        deployer = _deployer;
    }

    function approve(uint256 _amount) external {
        // Approve the Ethena staking contract to spend the specified amount of USDe on behalf of the client
        usdEToken.transferFrom(msg.sender, address(ethenaStaking), _amount);
    }

    function stake(uint256 _usdEAmount) external {
        // Ensure that the sender has approved the USDe tokens
        require(usdEToken.allowance(msg.sender, address(ethenaStaking)) >= _usdEAmount, "Approval not sufficient");

        // Send USDe to Ethena staking contract
        usdEToken.transferFrom(msg.sender, address(ethenaStaking), _usdEAmount);

        // Stake the USDe and receive sUSDe
        uint256 sUsdEAmount = ethenaStaking.stake(address(this), _usdEAmount);

        // Map the transaction values to the user's wallet
        userDeposits[msg.sender].usdEAmount = _usdEAmount;
        userDeposits[msg.sender].sUsdEAmount = sUsdEAmount;
    }

    function depositToAave(uint256 _usdEAmount, uint256 _leverage, uint256 _deadline, uint8 _permitV, bytes32 _permitR, bytes32 _permitS) external {
        // Ensure that only the deployer wallet can call this function
        require(msg.sender == deployer, "Only deployer can call this function");

        // Calculate the USDC amount (USD * leverage)
        uint256 usdcAmount = _usdEAmount * _leverage;

        // Approve Aave pool to spend USDC from the deployer using the permit function
        usdcTokenPermit.permit(address(this), aavePool, usdcAmount, _deadline, _permitV, _permitR, _permitS);

        // Deposit USDC into Aave pool (Aave will return aEthUSDC)
        (bool success, ) = aavePool.call{value: usdcAmount}(abi.encodeWithSignature("supply(address,uint256,address,uint16)", usdcToken, usdcAmount, address(this), 0));

        require(success, "Aave supply failed");

        // Receive the corresponding aEthUSDC tokens
        uint256 aEthUsdcAmount = usdcToken.balanceOf(address(this));  // Correctly use IERC20 for balanceOf

        // Store the deposit data (aEthUSDC amount) in the contract
        userDeposits[msg.sender].usdcAmount = usdcAmount;
        userDeposits[msg.sender].aEthUsdcAmount = aEthUsdcAmount;
    }

    function closeLoan() external {
        UserDeposit storage userDeposit = userDeposits[msg.sender];

        // 1. Unstake sUSDe and get back USDe
        ethenaStaking.stake(msg.sender, userDeposit.sUsdEAmount);

        // 2. Withdraw aEthUSDC from Aave
        (bool success, ) = aavePool.call{value: userDeposit.aEthUsdcAmount}(abi.encodeWithSignature("withdraw(address,uint256,address)", usdcToken, userDeposit.aEthUsdcAmount, address(this)));

        require(success, "Aave withdraw failed");

        // 3. Calculate the excess USDC (after interest)
        uint256 withdrawnUSDC = usdcToken.balanceOf(address(this));
        uint256 excessUSDC = withdrawnUSDC - userDeposit.usdcAmount;

        // 4. Send excess USDC back to the user
        usdcToken.transfer(msg.sender, excessUSDC);

        // Remove user deposit record
        delete userDeposits[msg.sender];
    }
}
